import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';  // Si usas React Router para pasar el ID

const Game1 = () => {
  const { exerciseId } = useParams();  // Usamos useParams para obtener el ID de la URL
  const [exercise, setExercise] = useState(null); // Estado para el ejercicio
  const [user, setUser] = useState(null); // Estado para el usuario
  const [experience, setExperience] = useState(0); // Puntos de experiencia del usuario
  const [level, setLevel] = useState(1); // Nivel del usuario

  useEffect(() => {
    // Obtener detalles del ejercicio desde la base de datos
    axios.get(`/api/exercises/${exerciseId}`)
      .then(response => {
        const exerciseData = response.data;
        setExercise(exerciseData);
      })
      .catch(error => console.error("Error al obtener el ejercicio:", error));

    // Obtener detalles del usuario, por ejemplo, el usuario con ID 1
    axios.get(`/api/users/1`) // Ajusta la ruta según la lógica de tu aplicación
      .then(response => {
        const userData = response.data;
        setUser(userData);
        setExperience(userData.experiencePoints); // Establecer los puntos de experiencia
        setLevel(userData.level); // Establecer el nivel del usuario
      })
      .catch(error => console.error("Error al obtener el usuario:", error));
  }, [exerciseId]); // Agregamos exerciseId como dependencia para recargar cuando cambie

  const handleSolveExercise = () => {
    // Aquí aumentamos los puntos de experiencia del usuario
    let newExperience = experience + 100; // Por ejemplo, sumamos 100 puntos al resolver el ejercicio
    setExperience(newExperience);

    // Verificar si el usuario ha alcanzado los 1000 puntos para subir de nivel
    if (newExperience >= 1000) {
      setLevel(level + 1);
      newExperience = 0; // Resetear los puntos de experiencia después de subir de nivel
    }

    // Aquí enviaríamos la actualización de los puntos de experiencia y nivel al backend
    axios.put(`/api/users/1`, {
      experiencePoints: newExperience,
      level: level,
    })
      .then(response => console.log("Usuario actualizado:", response))
      .catch(error => console.error("Error al actualizar el usuario:", error));
  };

  if (!exercise || !user) {
    return <div>Loading...</div>; // Mostrar un mensaje mientras cargan los datos
  }

  return (
    <div className="game-container">
      <h1>{exercise.name}</h1>
      <p>{exercise.description}</p>
      <div className="code-container">
        <pre>{exercise.answer.python}</pre>
      </div>
      <button onClick={handleSolveExercise}>Resolver ejercicio</button>
      <div className="user-info">
        <p>Puntos de experiencia: {experience}</p>
        <p>Nivel: {level}</p>
      </div>
    </div>
  );
};

export default Game1;
