import React, { useState } from "react";
import axios from "axios";

const EditorCodigo = ({ lenguaje }) => {
  const [codigo, setCodigo] = useState("");
  const [resultado, setResultado] = useState("");

  const enviarCodigo = async () => {
    try {
      const response = await axios.post("http://localhost:4000/api/evaluar", {
        codigo,
        lenguaje,
      });
      setResultado(response.data.mensaje);
    } catch (error) {
      console.error("Error al evaluar código:", error);
      setResultado("Error evaluando el código.");
    }
  };

  return (
    <div>
      <textarea
        rows="10"
        cols="50"
        value={codigo}
        onChange={(e) => setCodigo(e.target.value)}
        placeholder={`Escribe tu código en ${lenguaje}...`}
      />
      <br />
      <button onClick={enviarCodigo}>Enviar Código</button>
      {resultado && (
        <div>
          <h3>Resultado:</h3>
          <pre>{resultado}</pre>
        </div>
      )}
    </div>
  );
};

export default EditorCodigo;
