// src/components/Game1Exercise.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';
import UserInfo from './UserInfo';

const Game1Exercise = () => {
  const { exerciseId } = useParams();
  const [exercise, setExercise] = useState(null);
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const userId = "67869fc8efd086ba28f87d43"; // ID del usuario (puedes obtenerlo dinámicamente)

  useEffect(() => {
    const fetchExerciseData = async () => {
      try {
        const exerciseRes = await axios.get(`http://localhost:5000/api/exercises/codewars/${exerciseId}`);
        setExercise(exerciseRes.data);
        setLoading(false);
      } catch (err) {
        setError('Error al obtener el ejercicio');
        setLoading(false);
      }
    };

    const fetchUserData = async () => {
      try {
        const userRes = await axios.get(`http://localhost:5000/api/users/${userId}`);
        setUser(userRes.data);
      } catch (err) {
        console.error('Error al obtener el usuario');
      }
    };

    fetchExerciseData();
    fetchUserData();
  }, [codewarsId]);

  const handleGameCompletion = async () => {
    // Aquí puedes actualizar el progreso del usuario
    const experiencePointsEarned = 100; // Suponiendo que el jugador gana 100 puntos por completar el ejercicio
    try {
      await axios.put(`http://localhost:5000/api/users/${userId}/progress`, {
        experiencePoints: experiencePointsEarned
      });
    } catch (err) {
      console.error('Error al actualizar el progreso del usuario');
    }
  };

  if (loading) return <p>Loading...</p>;
  if (error) return <p>{error}</p>;

  return (
    <div>
      <h1>{exercise.title}</h1>
      <p>{exercise.description}</p>

      <div>
        <h2>Python Code</h2>
        <pre>{exercise.answer.python}</pre>
      </div>

      {/* Botón para completar el ejercicio */}
      <button onClick={handleGameCompletion}>Complete Game</button>

      {/* Mostrar la información del usuario */}
      {user && <UserInfo user={user} />}
    </div>
  );
};

export default Game1Exercise;
