import React, { useState, useEffect } from "react";
import axios from "axios";

function App() {
  const [ejercicio, setEjercicio] = useState({});
  const [codigo, setCodigo] = useState("");
  const [resultado, setResultado] = useState("");

  // Obtener un ejercicio desde el backend
  useEffect(() => {
    axios.get("http://localhost:4000/api/ejercicios").then((res) => {
      setEjercicio(res.data);
    });
  }, []);

  // Enviar código para evaluación
  const enviarCodigo = () => {
    axios
      .post("http://localhost:4000/api/evaluar", { codigo, lenguaje: 71 }) // 71: Python3 para Judge0
      .then((res) => {
        setResultado(res.data.mensaje);
      });
  };

  return (
    <div>
      <h1>{ejercicio.titulo}</h1>
      <p>{ejercicio.descripcion}</p>
      <textarea
        rows="10"
        cols="50"
        value={codigo}
        onChange={(e) => setCodigo(e.target.value)}
      />
      <button onClick={enviarCodigo}>Evaluar Código</button>
      <h2>Resultado: {resultado}</h2>
    </div>
  );
}

export default App;
