import React, { useState, useEffect } from "react";
import Ejercicio from "./components/Ejercicio";
import EditorCodigo from "./components/EditorCodigo";
import Resultado from "./components/Resultado";
import axios from "axios";
import "./App.css";

const App = () => {
  const [ejercicios, setEjercicios] = useState([]); // Lista de ejercicios obtenidos de Codewars
  const [ejercicioSeleccionado, setEjercicioSeleccionado] = useState(null); // Ejercicio seleccionado
  const [resultado, setResultado] = useState(""); // Resultado de evaluación

  // Obtener ejercicios desde la API de Codewars
  useEffect(() => {
    axios
      .get("http://localhost:4000/api/codewars") // Endpoint del backend para obtener ejercicios de Codewars
      .then((res) => {
        // Asumiendo que la respuesta contiene una lista de ejercicios
        const ejerciciosTransformados = res.data.map((ejercicio) => ({
          id: ejercicio.id,
          titulo: ejercicio.name,
          descripcion: ejercicio.description,
          lenguaje: ejercicio.languages[0], // Selecciona el primer lenguaje disponible
        }));
        setEjercicios(ejerciciosTransformados);
      })
      .catch((err) => console.error(err));
  }, []);

  // Manejar la selección de un ejercicio
  const seleccionarEjercicio = (ejercicio) => {
    setEjercicioSeleccionado(ejercicio);
    setResultado("");
  };

  // Manejar el envío de código al backend
  const enviarCodigo = (codigo) => {
    axios
      .post("http://localhost:4000/api/evaluar", {
        codigo,
        lenguaje: ejercicioSeleccionado.lenguaje, // Lenguaje del ejercicio seleccionado
      })
      .then((res) => setResultado(res.data.mensaje))
      .catch((err) => console.error(err));
  };

  return (
    <div>
      <h1>Aprende Programación con Codewars</h1>
      {!ejercicioSeleccionado ? (
        <div>
          <h2>Selecciona un ejercicio:</h2>
          {ejercicios.map((ejercicio) => (
            <Ejercicio
              key={ejercicio.id} // Usa `id` proporcionado por Codewars
              ejercicio={ejercicio}
              onSeleccionar={() => seleccionarEjercicio(ejercicio)}
            />
          ))}
        </div>
      ) : (
        <div>
          <h2>{ejercicioSeleccionado.titulo}</h2>
          <p>{ejercicioSeleccionado.descripcion}</p>
          <EditorCodigo
            onEnviar={enviarCodigo}
            lenguaje={ejercicioSeleccionado.lenguaje}
          />
          <Resultado resultado={resultado} />
          <button onClick={() => setEjercicioSeleccionado(null)}>Volver</button>
        </div>
      )}
    </div>
  );
};

export default App;
