import React, { useState, useEffect } from "react";
import Ejercicio from "./components/Ejercicio";
import EditorCodigo from "./components/EditorCodigo";
import Resultado from "./components/Resultado";
import axios from "axios";
import "./App.css";

const App = () => {
  const [ejercicios, setEjercicios] = useState([]);
  const [ejercicioSeleccionado, setEjercicioSeleccionado] = useState(null);
  const [resultado, setResultado] = useState("");

  // Obtener ejercicios desde el backend
  useEffect(() => {
    axios.get("http://localhost:4000/api/codewars/AvilesDanie/completed")
      .then((res) => {
        setEjercicios(res.data); // Guarda la lista de ejercicios completados
      })
      .catch((err) => console.error(err));
  }, []);
  
  

  // Manejar selección de ejercicio
  const seleccionarEjercicio = (ejercicio) => {
    setEjercicioSeleccionado(ejercicio);
    setResultado("");
  };

  // Manejar envío de código al backend
  const enviarCodigo = (codigo) => {
    axios.post("http://localhost:4000/api/evaluar", {
      codigo,
      lenguaje: ejercicioSeleccionado.lenguaje, // Lenguaje viene de ejercicio seleccionado
    })
      .then((res) => setResultado(res.data.mensaje))
      .catch((err) => console.error(err));
  };

  return (
    <div>
      <h1>Aprende Programación</h1>
      {!ejercicioSeleccionado ? (
        <div>
          <h2>Selecciona un ejercicio:</h2>
          {ejercicios.map((ejercicio) => (
            <button
              key={ejercicio.id}
              onClick={() => obtenerEjercicio(ejercicio.id)} // Obtiene los detalles del ejercicio por ID
            >
              {ejercicio.name} {/* Muestra el nombre del ejercicio */}
            </button>
          ))}
        </div>
      
      ) : (
        <div>
          <h2>{ejercicioSeleccionado.titulo}</h2>
          <p>{ejercicioSeleccionado.descripcion}</p>
          {/* Pasar onEnviar y lenguaje al EditorCodigo */}
          <EditorCodigo 
            onEnviar={enviarCodigo} 
            lenguaje={ejercicioSeleccionado.lenguaje} 
          />
          <Resultado resultado={resultado} />
          <button onClick={() => setEjercicioSeleccionado(null)}>Volver</button>
        </div>
      )}
    </div>
  );
};

export default App;
