import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";

const Game1 = () => {
  const { exerciseId } = useParams(); // Obtener el ID del ejercicio desde la URL
  const [exercise, setExercise] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchExerciseDetails = async () => {
      try {
        const response = await fetch(`http://localhost:5000/api/exercises/${exerciseId}`);
        if (!response.ok) {
          throw new Error("Failed to fetch exercise details");
        }
        const data = await response.json();
        setExercise(data);
        setLoading(false);
      } catch (err) {
        setError(err.message);
        setLoading(false);
      }
    };

    fetchExerciseDetails();
  }, [exerciseId]);

  if (loading) return <p>Loading exercise details...</p>;
  if (error) return <p>Error: {error}</p>;

  return (
    <div>
      <h1>{exercise.codewarsDetails.name}</h1>
      <p><strong>Description:</strong> {exercise.codewarsDetails.description}</p>
      <p><strong>Difficulty:</strong> {exercise.codewarsDetails.rank.name}</p>
      <pre><code>{exercise.answer.python}</code></pre>
      {/* Aquí puedes agregar la lógica del juego 1 */}
    </div>
  );
};

export default Game1;
