import React, { useEffect, useState } from "react";
import axios from "axios";

const RankingPage = () => {
  const [users, setUsers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    // Obtener usuarios desde el backend
    const fetchUsers = async () => {
      try {
        const response = await axios.get("/users");
        // Ordenar usuarios por nivel y puntos de experiencia
        const sortedUsers = response.data.sort(
          (a, b) =>
            b.level - a.level || b.experiencePoints - a.experiencePoints
        );
        setUsers(sortedUsers);
        setLoading(false);
      } catch (err) {
        setError("Error al cargar los datos del ranking.");
        setLoading(false);
      }
    };

    fetchUsers();
  }, []);

  if (loading) return <p>Cargando...</p>;
  if (error) return <p>{error}</p>;

  return (
    <div className="ranking-page">
      <h1>Ranking de Usuarios</h1>
      <table className="ranking-table">
        <thead>
          <tr>
            <th>#</th>
            <th>Usuario</th>
            <th>Nivel</th>
            <th>Puntos de Experiencia</th>
          </tr>
        </thead>
        <tbody>
          {users.map((user, index) => (
            <tr key={user._id}>
              <td>{index + 1}</td>
              <td>{user.username}</td>
              <td>{user.level}</td>
              <td>{user.experiencePoints}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default RankingPage;
