import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Game1 = () => {
  const [exercise, setExercise] = useState(null);
  const [user, setUser] = useState(null);
  const [userCompleted, setUserCompleted] = useState(false);  // To track if the user completed the exercise
  const [code, setCode] = useState('');
  const [message, setMessage] = useState('');

  const exerciseId = '6786b62613e6d66fbe3967e7';  // ID del ejercicio seleccionado (esto puede ser dinámico)

  // Fetch user details and exercise details
  useEffect(() => {
    const fetchData = async () => {
      try {
        // Obtener el ejercicio
        const exerciseRes = await axios.get(`/api/exercises/${exerciseId}`);
        setExercise(exerciseRes.data);

        // Obtener detalles del usuario
        const userRes = await axios.get(`/api/users/67869f7defd086ba28f87d41`);  // Usando el ID del usuario
        setUser(userRes.data);
      } catch (error) {
        console.error("Error fetching data:", error);
      }
    };

    fetchData();
  }, []);

  const handleSubmit = async () => {
    if (code === exercise.answer.python) {
      setMessage("¡Correcto!");
      setUserCompleted(true);

      // Actualizar puntos de experiencia y nivel
      const updatedUser = { ...user, experiencePoints: user.experiencePoints + 10 };
      if (updatedUser.experiencePoints >= 1000) {
        updatedUser.level += 1;  // Aumentar nivel
        updatedUser.experiencePoints = 0;  // Resetear puntos de experiencia después de subir de nivel
      }

      // Actualizar usuario en la base de datos
      await axios.put(`/api/users/${user._id}`, updatedUser);
      setUser(updatedUser);
    } else {
      setMessage("Intenta de nuevo.");
    }
  };

  if (!exercise || !user) {
    return <div>Cargando...</div>;
  }

  return (
    <div className="game-container">
      <div className="exercise-info">
        <h2>{exercise.name}</h2>
        <p>{exercise.description}</p>
      </div>

      <div className="code-container">
        <h3>Completa el código:</h3>
        <pre>{exercise.answer.python}</pre>
      </div>

      <div className="user-info">
        <h3>Usuario: {user.username}</h3>
        <p>Experiencia: {user.experiencePoints}</p>
        <p>Nivel: {user.level}</p>
      </div>

      <div className="input-container">
        <textarea
          value={code}
          onChange={(e) => setCode(e.target.value)}
          placeholder="Escribe tu código aquí..."
        ></textarea>
        <button onClick={handleSubmit}>Enviar</button>
      </div>

      <div className="message">
        <p>{message}</p>
      </div>
    </div>
  );
};

export default Game1;
