import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams, useNavigate } from 'react-router-dom';

const Game3 = () => {
  const { exerciseId } = useParams(); // Captura el ID del ejercicio desde la URL
  const [exercise, setExercise] = useState(null);
  const [randomAnswers, setRandomAnswers] = useState([]);
  const [options, setOptions] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [gameResult, setGameResult] = useState(null);
  const userId = "67869f7defd086ba28f87d41"; // ID del usuario (puedes obtenerlo din√°micamente)
  const navigate = useNavigate();

  useEffect(() => {
    const fetchData = async () => {
      try {
        // Obtener los detalles del ejercicio actual
        const exerciseRes = await axios.get(`http://localhost:5000/api/exercises/${exerciseId}`);
        const correctAnswer = exerciseRes.data.answer.python; // Soluci√≥n correcta
        setExercise(exerciseRes.data);

        // Obtener tres respuestas aleatorias de otros ejercicios
        const randomAnswersRes = await axios.get(
          `http://localhost:5000/api/exercises/random-answers?exclude=${exerciseId}&limit=3`
        );
        const incorrectAnswers = randomAnswersRes.data.map((ex) => ex.answer.python);

        // Combinar las respuestas y aleatorizarlas
        const combinedOptions = [...incorrectAnswers, correctAnswer].sort(() => Math.random() - 0.5);
        setRandomAnswers(incorrectAnswers);
        setOptions(combinedOptions);

        setLoading(false);
      } catch (err) {
        setError('Error al cargar los datos del ejercicio');
        setLoading(false);
      }
    };

    fetchData();
  }, [exerciseId]);

  const handleAnswerSelection = async (selectedAnswer) => {
    const isCorrect = selectedAnswer === exercise.answer.python;

    // Actualizar el progreso del usuario
    try {
      const payload = {
        userId,
        exerciseId,
        experiencePoints: isCorrect ? 100 : 0,
        successful: isCorrect,
      };

      await axios.put(`http://localhost:5000/api/users/progress-unified`, payload);

      setGameResult(isCorrect ? 'win' : 'lose');
    } catch (err) {
      console.error('Error al actualizar el progreso:', err);
      setError('Error al actualizar el progreso del usuario');
    }
  };

  // Funci√≥n para volver a la lista de ejercicios
  const goToExerciseList = () => {
    navigate('/game/game3');
  };

  // Funci√≥n para reiniciar el juego
  const restartGame = () => {
    setGameResult(null);
    setOptions([...options].sort(() => Math.random() - 0.5)); // Reordenar aleatoriamente
  };

  if (loading) return <p>Loading...</p>;
  if (error) return <p>{error}</p>;

  if (!exercise) return <p>Exercise data not available.</p>;

  return (
    <div className="exercise-container">
      {/* Mostrar los detalles del ejercicio */}
      <div className="exercise-info">
        <h1>{exercise.name}</h1>
        <p>{exercise.description}</p>
      </div>

      {/* Mostrar las opciones de respuesta */}
      <div className="exercise-options">
        <h2>Select the correct solution:</h2>
        {options.map((option, index) => (
          <button
            key={index}
            onClick={() => handleAnswerSelection(option)}
            className="option-button"
          >
            {option}
          </button>
        ))}
      </div>

      {/* Mostrar el resultado del juego */}
      {gameResult && (
        <div className="result-modal">
          {gameResult === 'win' ? (
            <>
              <p>üéâ Correct! You won!</p>
              <button onClick={goToExerciseList}>Back to Exercise List</button>
            </>
          ) : (
            <>
              <p>‚ùå Incorrect! Try again!</p>
              <button onClick={restartGame}>Restart</button>
              <button onClick={goToExerciseList}>Back to Exercise List</button>
            </>
          )}
        </div>
      )}
    </div>
  );
};

export default Game3;
