import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams, useNavigate } from 'react-router-dom';

const Game1 = () => {
  const { exerciseId } = useParams(); // Obtener el ID del ejercicio desde la URL
  const [exercise, setExercise] = useState(null);
  const [code, setCode] = useState(''); // Aquí se guarda el código del ejercicio
  const [experiencePoints, setExperiencePoints] = useState(0);
  const [level, setLevel] = useState(1);
  const navigate = useNavigate();

  // Fetch del ejercicio desde la base de datos
  useEffect(() => {
    const fetchExercise = async () => {
      try {
        const response = await axios.get(`/api/exercises/${exerciseId}`);
        setExercise(response.data);
      } catch (error) {
        console.error("Error fetching exercise:", error);
      }
    };

    fetchExercise();
  }, [exerciseId]);

  // Función para manejar la respuesta del ejercicio (cuando el usuario complete el ejercicio)
  const handleSubmit = async () => {
    if (code.trim() !== '') {
      // Aquí puedes agregar la lógica de evaluación del código

      // Simulamos que el usuario obtiene puntos de experiencia
      const newExperiencePoints = experiencePoints + 100;
      setExperiencePoints(newExperiencePoints);

      // Si el usuario alcanza 1000 puntos, sube de nivel
      if (newExperiencePoints >= 1000) {
        setLevel(level + 1);
        setExperiencePoints(0); // Reiniciar puntos al subir de nivel
      }

      // Actualizar el usuario en la base de datos
      try {
        await axios.put(`/api/users/${exerciseId}`, {
          experiencePoints: newExperiencePoints,
          level: level + 1
        });
      } catch (error) {
        console.error("Error updating user:", error);
      }

      // Navegar al siguiente juego o página
      navigate(`/game${level + 1}`);
    }
  };

  if (!exercise) {
    return <div>Loading exercise...</div>;
  }

  return (
    <div>
      <h1>{exercise.name}</h1>
      <p>{exercise.description}</p>

      <div>
        <h2>Code Challenge</h2>
        <pre>{exercise.answer.python}</pre>
        <textarea
          value={code}
          onChange={(e) => setCode(e.target.value)}
          placeholder="Write your code here"
        />
      </div>

      <button onClick={handleSubmit}>Submit Code</button>

      <div>
        <h3>Experience: {experiencePoints} | Level: {level}</h3>
      </div>
    </div>
  );
};

export default Game1;
