import React, { useEffect, useState } from "react";

const ExerciseList = () => {
  const [exercises, setExercises] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchExercises = async () => {
      try {
        const response = await fetch("http://localhost:5000/api/exercises/details");
        if (!response.ok) {
          throw new Error("Failed to fetch exercises");
        }
        const data = await response.json();
        setExercises(data);
        setLoading(false);
      } catch (err) {
        setError(err.message);
        setLoading(false);
      }
    };

    fetchExercises();
  }, []);

  if (loading) return <p>Loading exercises...</p>;
  if (error) return <p>Error: {error}</p>;

  return (
    <div>
      <h1>Exercise List</h1>
      <ul>
        {exercises.map((exercise) => (
          <li key={exercise._id}>
            <h3>{exercise.codewarsDetails.name}</h3>
            <p>{exercise.codewarsDetails.description}</p>
            <p>Difficulty: {exercise.codewarsDetails.rank.name}</p>
            <a href={exercise.codewarsDetails.url} target="_blank" rel="noopener noreferrer">
              View on Codewars
            </a>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default ExerciseList;
