import React, { useState } from 'react';
import axios from 'axios';

function HomePage() {
  const [userData, setUserData] = useState(null);

  const fetchUserInfo = async () => {
    try {
      const userId = "67869f7defd086ba28f87d41"; // ID del usuario
      const response = await axios.get(`http://localhost:5000/api/users/${userId}`);
      setUserData(response.data);
    } catch (error) {
      console.error("Error fetching user data:", error);
      alert("Failed to fetch user data.");
    }
  };

  return (
    <div>
      <h1>Welcome to the Learning Platform</h1>
      <button onClick={fetchUserInfo}>Get User Info</button>
      {userData && (
        <div>
          <h2>User Information</h2>
          <p><strong>Username:</strong> {userData.username}</p>
          <p><strong>Level:</strong> {userData.level}</p>
          <p><strong>Experience Points:</strong> {userData.experiencePoints}</p>
          <p><strong>Progress:</strong> {userData.progress}%</p>
          <p><strong>Created At:</strong> {new Date(userData.createdAt).toLocaleString()}</p>
        </div>
      )}
    </div>
  );
}

export default HomePage;
